def detect_bias(user_query):
    """Detect potential bias in query and apply governance controls"""
    # Analyze specific bias types
    bias_types = []
    bias_triggers = {
        'gender_bias': ['male only', 'female only', 'men are better', 'women are better'],
        'age_bias': ['too old', 'too young', 'only hire young', 'younger employees'],
        'racial_bias': ['racial', 'ethnic background', 'culture fit'],
        'disability_bias': ['disability', 'handicap', 'special needs']
    }

    # Check for bias triggers in user query
    for bias_type, triggers in bias_triggers.items():
        if any(trigger in user_query.lower() for trigger in triggers):
            bias_types.append(bias_type)

    # Prepare bias detection result
    bias_detection = {
        'bias_detected': len(bias_types) > 0,
        'bias_types': bias_types,
        'requires_fairness_review': len(bias_types) > 0,
        'governance_controls': []
    }

    # Add appropriate governance controls
    if bias_detection['bias_detected']:
        bias_detection['governance_controls'].append('FAIRNESS_CHECK_REQUIRED')

    # Add governance metadata
    bias_detection['governance_tags'] = ["BiasAnalyzed"]
    if bias_detection['bias_detected']:
        bias_detection['governance_tags'].append("BiasDetected")

    bias_detection['original_query'] = user_query

    return bias_detection
